import { logger } from "../utils/logger.js";
export async function getText(page, selector) {
    try {
        logger.info(`Getting the text: ${selector}`);
        return await page.textContent(selector) ?? '';
    }
    catch (err) {
        logger.error(`Failed to get text from ${selector}: ${err.message}`);
        throw err;
    }
}
export async function getAttribute(page, selector, attribute) {
    try {
        logger.info(`Getting the attribute: ${selector}`);
        return await page.getAttribute(selector, attribute) ?? '';
    }
    catch (err) {
        logger.error(`Failed to get attibute from ${selector}: ${err.message}`);
        throw err;
    }
}
export async function getByRole(page, role, options) {
    try {
        logger.info(`Getting the role of element: ${role}`);
        return page.getByRole(role, options);
    }
    catch (err) {
        logger.error(`Failed to get role of the element: ${err.message}`);
        throw err;
    }
}
