import { logger } from "../utils/logger.js";
/**
 * Launches the application and verifies that the base URL loads successfully.
 *
 * This function is intended for initial app startup (not just navigation).
 * It waits until the DOM is loaded and ensures the page title or readiness
 * indicator (if provided) is visible.
 */
export async function launchURL(page, baseURL) {
    try {
        logger.info(`Launching application at: ${baseURL}`);
        await page.goto(baseURL, { waitUntil: "domcontentloaded", timeout: 120000 });
        // Optional sanity check â€” ensures something meaningful is rendered.
        const title = await page.title();
        logger.info(`Application launched successfully. Page title: "${title}"`);
    }
    catch (err) {
        logger.error(`Failed to launch application at ${baseURL}: ${err.message}`);
        throw err;
    }
}
