import { logger } from "../utils/logger.js";
/**
 * Selects an option in a dropdown by its value attribute.
 */
export async function selectOptionByValue(page, selector, value) {
    try {
        await page.selectOption(selector, { value });
        logger.info(`Selected option by value "${value}" in ${selector}`);
    }
    catch (err) {
        logger.error(`Failed to select option by value "${value}" in ${selector}: ${err.message}`);
        throw err;
    }
}
/**
 * Selects an option in a dropdown by its visible label text.
 */
export async function selectOptionByLabel(page, selector, label) {
    try {
        await page.selectOption(selector, { label });
        logger.info(`Selected option by label "${label}" in ${selector}`);
    }
    catch (err) {
        logger.error(`Failed to select option by label "${label}" in ${selector}: ${err.message}`);
        throw err;
    }
}
