import { logger } from "../utils/logger.js";
/**
 * Waits until the element is visible.
 */
export async function waitForElementToVisible(page, selector, label) {
    try {
        const isVisible = await page.locator(selector).isVisible();
        if (isVisible) {
            logger.info(`[${label}] Element is visible: ${selector}`);
        }
        else {
            logger.warn(`[${label}] Element not visible yet: ${selector}`);
        }
    }
    catch (err) {
        logger.error(`[${label}] Failed to verify visibility for ${selector}: ${err.message}`);
        throw err;
    }
}
