import { BrowserManager } from "../BrowserManager";

export async function getText(selector: string): Promise<string> {
    const page = await BrowserManager.getPage();
    return await page.textContent(selector) ?? '';
}

export async function getAttribute(selector: string, attribute: string): Promise<string> {
    const page = await BrowserManager.getPage();
    return await page.getAttribute(selector, attribute) ?? '';
}

export async function getByRole(role: string, options?: { name?: string }) {
    const page = await BrowserManager.getPage();
    return page.getByRole(role as any, options);
}
