import type { Page } from "@playwright/test";

export async function getText(page: Page, selector: string): Promise<string> {
    return await page.textContent(selector) ?? '';
}

export async function getAttribute(page: Page, selector: string, attribute: string): Promise<string> {
    return await page.getAttribute(selector, attribute) ?? '';
}

export async function getByRole(page: Page, role: string, options?: { name?: string }) {
    return page.getByRole(role as any, options);
}
