import { BrowserManager } from "../BrowserManager";

export async function hover(selector: string) {
    const page = await BrowserManager.getPage();
    await page.hover(selector);
}

export async function scrollIntoView(selector: string) {
    const page = await BrowserManager.getPage();
    await page.locator(selector).scrollIntoViewIfNeeded();
}

export async function dragAndDrop(source: string, target: string) {
    const page = await BrowserManager.getPage();
    await page.dragAndDrop(source, target);
}

export async function check(selector: string) {
    const page = await BrowserManager.getPage();
    await page.check(selector);
}

export async function uncheck(selector: string) {
    const page = await BrowserManager.getPage();
    await page.uncheck(selector);
}




