import type { Page } from "@playwright/test";

export async function hover(page: Page, selector: string) {
    await page.hover(selector);
}

export async function scrollIntoView(page: Page, selector: string) {
    await page.locator(selector).scrollIntoViewIfNeeded();
}

export async function dragAndDrop(page: Page, source: string, target: string) {
    await page.dragAndDrop(source, target);
}

export async function check(page: Page, selector: string) {
    await page.check(selector);
}

export async function uncheck(page: Page, selector: string) {
    await page.uncheck(selector);
}




