import type { Page } from "@playwright/test";

interface RefreshOptions {
    waitUntil?: 'load' | 'domcontentloaded' | 'networkidle';
    timeout?: number;
    ignoreCache?: boolean;
}
export async function navigateTo(page: Page, url: string) {
    await page.goto(url);
}

export async function refreshPage(page: Page, options?: RefreshOptions) {
    await page.reload({
        waitUntil: options?.waitUntil || 'load',
        timeout: options?.timeout,
    });
}

export async function navigatBack(page: Page, options?: RefreshOptions) {
    await page.goBack({
        waitUntil: options?.waitUntil || 'load',
        timeout: options?.timeout,
    });
}

export async function navigatForward(page: Page, options?: RefreshOptions) {
    await page.goForward({
        waitUntil: options?.waitUntil || 'load',
        timeout: options?.timeout,
    });
}