import type { Page } from "@playwright/test";
export async function type(page: Page, selector: string, text: string) {
    await page.fill(selector, text);
}

export async function clearAndType(page: Page, selector: string, text: string) {
    await page.fill(selector, '');
    await page.fill(selector, text);
}

export async function typeWithDelay(page: Page, selector: string, text: string, delay: number = 100) {
    await page.type(selector, text, { delay });
}

export async function pressKey(page: Page, selector: string, key: string) {
    await page.press(selector, key);
}
