import { BrowserManager } from "../BrowserManager";

export async function type(selector: string, text: string) {
    const page = await BrowserManager.getPage();
    await page.fill(selector, text);
}

export async function clearAndType(selector: string, text: string) {
    const page = await BrowserManager.getPage();
    await page.fill(selector, '');
    await page.fill(selector, text);
}

export async function typeWithDelay(selector: string, text: string, delay: number = 100) {
    const page = await BrowserManager.getPage();
    await page.type(selector, text, { delay });
}

export async function pressKey(selector: string, key: string) {
    const page = await BrowserManager.getPage();
    await page.press(selector, key);
}
